  const [inputText, setinputText] = useState("");
  const [toolView, settoolView] = useState("MainTool");
  const apiEndPoint = "https://localhost:3000/api/add";

  const postData = async (event) => {
    event.preventDefault();
    const inputTextData = {
      key,
      inputText,
    };
