import styles from "./styles/Tool.module.css";
import Image from "next/image";
import { useState, useEffect } from "react";
import axios from "axios";
import Featured from "./Featured";

const Tool = (props) => {
  const inputTextData = {
    txt: props.Data,
  };
  let textOneWord;
  let textTwoWord;
  let textThreeWord;

  const [TextData, setTextData] = useState([]);

  const kdaOutput = async () => {
    const res = await axios.post(
      process.env.NEXT_PUBLIC_API_URL,
      inputTextData
    );
    setTextData(res.data);
    console.log(TextData);
    textOneWord = res.data[2].OneWord;
    textTwoWord = res.data[3].TwoWord;
    textThreeWord = res.data[4].ThreeWord;
    console.log(textTwoWord)
    console.log(textThreeWord)
  };
  useEffect(() => {
    kdaOutput();
  }, []);

  const dataDisplay = () =>{
    const [TextData, setTextData] = useState([]);

  const kdaOutput = async () => {
    const res = await axios.post(
      process.env.NEXT_PUBLIC_API_URL,
      inputTextData
    );
    setTextData(res.data);
    console.log(TextData);
    let textOneWord = res.data[2].OneWord;
    let textTwoWord = res.data[3].TwoWord;
    let textThreeWord = res.data[4].ThreeWord;
    console.log(textOneWord)
    console.log(textThreeWord)
  };
  useEffect(() => {
    kdaOutput();
  }, []);


    const DisplayDataOne=textOneWord.map(
        (info)=>{
            return(
                <tr>
                    <td>{info.count}</td>
                    <td>{info.density}</td>
                    <td>{info.keyword}</td>
                </tr>
            )
        }
    )
    const DisplayDataTwo=textTwoWord.map(
      (info)=>{
          return(
              <tr>
                  <td>{info.count}</td>
                  <td>{info.density}</td>
                  <td>{info.keyword}</td>
              </tr>
          )
      }
  )
  const DisplayDataThree=textThreeWord.map(
    (info)=>{
        return(
            <tr>
                <td>{info.count}</td>
                <td>{info.density}</td>
                <td>{info.keyword}</td>
            </tr>
        )
    }
)

    return(
        <div>
            <table class={styles.analysis_table}>
                  <thead>
                    <tr class={styles.tableheader}>
                      <th>One Word</th>
                      <th>Count</th>
                      <th>Density</th>
                      <th>Two Word</th>
                      <th>Count</th>
                      <th>Density</th>
                      <th>Three Word</th>
                      <th>Count</th>
                      <th>Density</th>
                    </tr>
                  </thead>

                  <tbody>


                    {DisplayDataOne}
                    {DisplayDataTwo}
                    {DisplayDataThree}

                </tbody>
            </table>

        </div>
    )

 }


  return (
    <>
      {TextData.length && (
        <div>
          <div className={styles.container}>
            <div class={styles.heading}>
              <h1>Keyword Density Analyser</h1>
            </div>
            <div class={styles.frequency}>
              <div class={styles.frequency_text}>
                <h2>
                  {TextData[0].no_of_words} Words , {TextData[1].no_of_chars}{" "}
                  Characters
                </h2>
              </div>
            </div>
            <div class={styles.textbox}>
              <div class={styles.textbox_text}>
                <p>{props.Data}</p>
              </div>
            </div>
            <div class={styles.analysis}>
              <div>
                {Da}
